from pydantic import BaseModel
from typing import Optional


class WeatherData(BaseModel):
    city: str
    temperature: float
    description: str
    humidity: int
    wind_speed: float

    class Config:
        schema_extra = {
            "example": {
                "city": "London",
                "temperature": 15.5,
                "description": "Partly cloudy",
                "humidity": 65,
                "wind_speed": 12.3
            }
        }


class WeatherResponse(WeatherData):
    pass
